// id and name of savingStatus iframe.
var windowName;

// Dispatch message from Global HTML.
function handleMessage(event) {
  if (window.top !== window)
    return;
  if (event.name !== "savingStatus")
    return;
  var message = event.message;
  if (message === "saving")
  {
    showSavingStatus();
  }
  else if (message === "saved")
  {
    hideSpinner();
    updateSavingStatus("Saved");
    setTimeout("hideSavingStatus()", 550);
  }
  else if (message === "failed")
  {
    hideSpinner();
    updateSavingStatus("Failed");
    setTimeout("hideSavingStatus()", 1000);
  }
}
safari.self.addEventListener("message", handleMessage, false);

// Totally lifted this from Instapaper.
// Shows the little "Instapaper: Saving" box with the spinner in the top-left
// corner.
function showSavingStatus() {
  var i = document.createElement('iframe');
  // Ensure unique id/name for iframe.
  for (;;)
  {
    windowName = makeWindowName();
    if (!document.getElementById(windowName)) break;
  }
  // This was all taken from Instapaper.
  i.setAttribute('name', windowName);
  i.setAttribute('id', windowName);
  i.setAttribute('style', "z-index: 2147483647; position: fixed; left: 10px; top: 10px; width: 168px; height: 100px; border: 3px solid $aaa;");
  document.body.appendChild(i);
  window[windowName].document.write('<html>' + 
    '<body style="color: #555; background-color: #fff; text-align: center; ' + 
    'margin: 0px; font-family: Georgia, Times, serif; font-size: 26px;">' + 
    '<img style="display: block; position: fixed; bottom: 4px; left: 72px;"' + 
    ' id="spinner"  src="data:image/gif;base64,"/>' + 
    '<div style="text-align: center; width: 80%; padding-bottom: 1px; margin: 0 auto 15px auto; font-size: 13px; border-bottom: 1px solid #ccc; color: #333;">Instapaper</div>' +
    '<span id="savingStatus">Saving...</span>' +
    '</body></html>'
  );
}

// Change the text of the saving status box (is "Saving..." when box is 
// originally shown).
function updateSavingStatus(statusText) {
  var ss = window[windowName].document.getElementById('savingStatus');
  ss.innerText = statusText;
}

// Stop displaying the spinner.
function hideSpinner() {
  var spinner = window[windowName].document.getElementById('spinner');
  spinner.style.display = "none";
}

// Hide, then remove, the saving status box.
function hideSavingStatus() {
  var f = document.getElementById(windowName);
  f.style.display = "none";
  f.parentNode.removeChild(f);
}

// Returns a random string of 10 upperalphanumeric characters.
function makeWindowName() {
  var text = "";
  var possible = "ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789";
  for(var i = 0; i < 10; i++)
  {
    text += possible.charAt(Math.floor(Math.random() * possible.length));
  }
  return text;
}
